mapper.update:
update community 
set comm_title=#{commTitle}, comm_cont=#{commCont} 
where comm_no=#{commNo}

update community 
set comm_hit = comm_hit+1 
where comm_no=#{commNo}

mapper.select:
select * from ( select rownum rn, comm_no, member_id, comm_title, comm_cont, comm_date, comm_hit, reply_count from ( select comm_no, member_id, comm_title, comm_cont, comm_date, comm_hit, reply_count from community
where ${column} like #{word}
order by comm_no desc ) community ) where rn between #{startRow} and #{endRow}

select count(*) 
from community


	where ${column} like #{word}

mapper.update:
update community c
set c.REPLY_COUNT = (select count(COMM_RE_NO) from community_reply where COMM_NO=#{commNo}) 
where c.COMM_NO=#{commNo}
