mapper.insert:
insert into member ( MEMBER_ID, MEMBER_PW, MEMBER_EMAIL, MEMBER_PHONE, MEMBER_NAME, MEMBER_PCODE, MEMBER_ADDRESS1, MEMBER_ADDRESS2, MEMBER_STATUS, MEMBER_REG_DATE, MEMBER_LAST_LOGIN
) values ( #{memberId}, #{memberPw}, #{memberEmail}, #{memberPhone}, #{memberName}, #{memberPcode}, #{memberAddress1}, #{memberAddress2},
1, SYSDATE, #{memberLastLogin}
)

mapper.select:
select memberid, memberpw,memberemail, memberphone, membername,
 memberpcode, memberaddress1, memberaddress2, memberstatus, memberregdate, memberlastlogin  
from member where memberid=#{memberid}
